mapper.select:
select

from T_RULE_REGULATION


       AND SEND_DATE >= #{startDate}


       AND SEND_DATE <= #{endDate}


		AND remove=#{remove, jdbcType=VARCHAR}


ORDER BY SEND_DATE DESC

mapper.update:
UPDATE T_RULE_REGULATION SET
UPDATE_BY = #{updateBy,jdbcType=VARCHAR},
UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}, REMOVE = '1' WHERE ID = #{id}
